typedef long long ll;
#define f0(x,n) for(int x=0;x<n;x++)
#define f1(x,n) for(int x=1;x<=n;x++)
#define sz(x) ((int)(x).size())
#define all(x) (x).begin(),(x).end()
#define pb push_back
#define mp make_pair
#define fi first
#define se second
#define vec vector
typedef double ld;
typedef pair<int,int> pii;
typedef pair<ll,ll> pll;
typedef vec<int> vi;
typedef vec<ll> vl;
const int dx[]={1,1,-1,-1},dy[]={1,-1,-1,1};
int mxp[4][123456];
int mxs[4][123456];
const int inf=1e9;
class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n=p.size();
        #define S(i) p[(i)-1]
        for(int s=0;s<4;++s) {
            mxp[s][0]=-inf;
            for(int i=1;i<=n;++i) {
                mxp[s][i]=max(mxp[s][i-1],S(i)[0]*dx[s]+S(i)[1]*dy[s]);
            }
            mxs[s][n+1]=-inf;
            for(int i=n;i>=1;--i) {
                mxs[s][i]=max(mxs[s][i+1],S(i)[0]*dx[s]+S(i)[1]*dy[s]);
            }
        }
        int ans=2e9;
        for(int i=1;i<=n;++i) {
            int a=0;
            for(int s=0;s<4;++s) {
                int mx=max(mxp[s][i-1],mxs[s][i+1]);
                int mi=-max(mxp[s^2][i-1],mxs[s^2][i+1]);
                a=max(a,mx-mi);
            }
            ans=min(ans,a);
        }
        return ans;
    }
};
