class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        int n=a.size();
        multiset<int>s1,s2;
        for(auto &v:a)
        {
            int x=v[0],y=v[1];
            s1.insert(x-y);
            s2.insert(x+y);
        }
        int ans=INT_MAX;
        for(int i=0;i<n;i++)
        {
            int x=a[i][0],y=a[i][1];
            int k1=x-y,k2=x+y;
            s1.erase(s1.find(k1));
            s2.erase(s2.find(k2));
            ans=min(ans,max(*s1.rbegin()-*s1.begin(),*s2.rbegin()-*s2.begin()));
            s1.insert(k1);
            s2.insert(k2);
        }
        return ans;
    }
};
