class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {

        vector<long long> sum;
        vector<long long> dif;
        for(auto& it : points){
            sum.push_back(it[0]+it[1]);
            dif.push_back(it[0]-it[1]);
        }
        sort(sum.begin(), sum.end());
        sort(dif.begin(), dif.end());

        long long sol = max(sum[points.size()-1]-sum[0], dif[points.size()-1]-dif[0]);
        for(auto &it : points){
            long long l = 0;
            long long r = points.size()-1;
            long long l2 = 0;
            long long r2 = points.size()-1;

            long long sum2 = it[0]+it[1];
            long long dif2 = it[0]-it[1];

            if(sum[l]==sum2) l++;
            else if(sum[r]==sum2) r--;

            if(dif[l2]==dif2) l2++;
            else if(dif[r2]==dif2) r2--;

            sol = min(sol, max(sum[r]-sum[l], dif[r2]-dif[l2]));

        }
        return sol;
    }
};
