class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {

        int n = points.size();

        vector<int> v1,v2;
        for(int i=0; i<n; i++){
            v1.push_back(points[i][0]+points[i][1]);
            v2.push_back(points[i][0]-points[i][1]);
        }

        sort(v1.begin(), v1.end());
        sort(v2.begin(), v2.end());

        int ans = INT_MAX;
        for(int i=0; i<n; i++){
            int a = points[i][0]+points[i][1];
            int b = points[i][0]-points[i][1];

            int s=0,e=n-1;
            if(v1[s]==a){
                s++;
            }
            else if(v1[e]==a){
                e--;
            }

            int temp1 = v1[e]-v1[s];

            s=0,e=n-1;
            if(v2[s]==b){
                s++;
            }
            else if(v2[e]==b){
                e--;
            }

            int temp2 = v2[e]-v2[s];

            ans = min(ans,max(temp1,temp2));
        }

        return ans;
    }
};
