class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        map<long long, int> a, s;
        for (auto &p : points) a[p[0]+p[1]] += 1;
        for (auto &p : points) s[p[0]-p[1]] += 1;

        long long ans = 10000000000;
        for (auto &p : points) {
            a[p[0]+p[1]] -= 1;
            if (a[p[0]+p[1]] <= 0) a.erase(p[0]+p[1]); 
            s[p[0]-p[1]] -= 1;
            if (s[p[0]-p[1]] <= 0) s.erase(p[0]-p[1]); 

            long long dist = 0;
            auto it = a.end();
            it--;
            dist = it->first - (a.begin()->first);
            auto it2 = s.end();
            it2--;

            long long dist2 = it2->first - (s.begin()->first);
            dist = max(dist, dist2);
            ans = min(ans, dist);

            a[p[0]+p[1]] += 1;
            s[p[0]-p[1]] += 1;
        }

        return ans;
    }
};
