def max(a, b):
    return a if a > b else b


def min(a, b):
    return a if a < b else b


class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        n = len(points)
        points.sort()
        pos = [-inf, -1]
        neg = [-inf, -1]
        res = [-inf, (-1, -1)]
        for i in range(n):
            x, y = points[i]
            if pos[0] - y + x > res[0]:
                res = [pos[0]-y+x, (pos[1], i)]
            if neg[0] + y + x > res[0]:
                res = [neg[0] + y + x, (neg[1], i)]
            if y-x > pos[0]:
                pos = [y-x, i]
            if -y-x > neg[0]:
                neg = [-y-x, i]

        ans = inf
        lst = res[1][:]
        for xx in lst:
            pos = [-inf, -1]
            neg = [-inf, -1]
            res = [-inf, (-1, -1)]
            for i in range(n):
                if i == xx:
                    continue
                x, y = points[i]
                if pos[0] - y + x > res[0]:
                    res = [pos[0] - y + x, (pos[1], i)]
                if neg[0] + y + x > res[0]:
                    res = [neg[0] + y + x, (neg[1], i)]
                if y - x > pos[0]:
                    pos = [y - x, i]
                if -y - x > neg[0]:
                    neg = [-y - x, i]
            ans = min(ans, res[0])

        return ans
