class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {
        multiset<int> m, n;

        for(auto& e : a) {
            int x = e[0], y = e[1];
            m.insert(x + y);
            n.insert(x - y);

        }
        int ans = 1e9;
        for(auto& e : a) {
            int x = e[0], y = e[1];
            m.erase(m.find(x + y));
            n.erase(n.find(x - y));


            ans = min(ans, max(*m.rbegin() - *m.begin(), *n.rbegin() - *n.begin()));

            m.insert(x + y);
            n.insert(x - y);
        }
        return ans;
    }
};
