class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int res=1e9;
        multiset<int>x,y;
        for(auto v:points) x.insert(v[0]+v[1]),y.insert(v[0]-v[1]);
        for(auto v:points){
            x.erase(x.find(v[0]+v[1]));
            y.erase(y.find(v[0]-v[1]));
            res=min(res,max(*x.rbegin()-*x.begin(),*y.rbegin()-*y.begin()));
            x.insert(v[0]+v[1]);
            y.insert(v[0]-v[1]);
        }
        return res;
    }
};
