class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int ans=0;
        int n = points.size();
        vector<vector<int>> vp1, vp2;
        for(int i=0; i<n; i++) {
            vp1.push_back({points[i][0]+points[i][1], i});
            vp2.push_back({points[i][0]-points[i][1], i});
        }

        sort(vp1.begin(), vp1.end());
        sort(vp2.begin(), vp2.end());

        int a=0, b=0;
        int num1 = vp1[n-1][0]-vp1[0][0], num2 = vp2[n-1][0]-vp2[0][0];
        if(num1 < num2) {
            a = vp2[n-1][1];
            b = vp2[0][1];
            ans = num2;
        }
        else {
            a = vp1[n-1][1];
            b = vp1[0][1];
            ans = num1;
        }

        vp1.clear();
        vp2.clear();

        for(int i=0; i<n; i++) {
            if(i==a) continue;
            vp1.push_back({points[i][0]+points[i][1], i});
            vp2.push_back({points[i][0]-points[i][1], i});
        }

        sort(vp1.begin(), vp1.end());
        sort(vp2.begin(), vp2.end());
        num1 = vp1[n-2][0]-vp1[0][0], num2 = vp2[n-2][0]-vp2[0][0];
        ans = min(ans, max(num1, num2));

        vp1.clear();
        vp2.clear();

        for(int i=0; i<n; i++) {
            if(i==b) continue;
            vp1.push_back({points[i][0]+points[i][1], i});
            vp2.push_back({points[i][0]-points[i][1], i});
        }

        sort(vp1.begin(), vp1.end());
        sort(vp2.begin(), vp2.end());
        num1 = vp1[n-2][0]-vp1[0][0], num2 = vp2[n-2][0]-vp2[0][0];
        ans = min(ans, max(num1, num2));

        return ans;
    }
};
