class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> add,diff;
        int n=points.size();
        vector<pair<int,int>> v;
        for(auto x:points){
            add.insert(x[0]+x[1]);
            diff.insert(x[0]-x[1]);
            v.push_back({x[0]+x[1],x[0]-x[1]});
        }
        int ans=1e9;
        for(auto x:v){
            auto it=add.find(x.first),it2=diff.find(x.second);
            add.erase(it);
            diff.erase(it2);
            ans=min(ans,max(*(--add.end())-*add.begin(),*(--diff.end())-*diff.begin()));
            add.insert(x.first);
            diff.insert(x.second);
        }
        return ans;
    }
};
