class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        n = len(points)
        sumlis = [0]*n
        difflis = [0]*n
        for i in range(n):
            sumlis[i] = (points[i][0]+points[i][1], i)
            difflis[i] = (points[i][0]-points[i][1], i)
        sumlis.sort()
        difflis.sort()
        sumd = {}
        diffd = {}
        for i in range(n):
            sumd[sumlis[i][1]] = i
            diffd[difflis[i][1]] = i
        tmini = max(sumlis[-1][0]-sumlis[0][0], difflis[-1][0]-difflis[0][0])
        for i in range(n):
            inx1 = sumd[i]
            maxi = 0
            if inx1==0:
                maxi = max(maxi, sumlis[-1][0]-sumlis[1][0])
            elif inx1==n-1:
                maxi = max(maxi, sumlis[-2][0]-sumlis[0][0])
            else:
                maxi = max(maxi, sumlis[-1][0]-sumlis[0][0])
            inx2 = diffd[i]
            if inx2==0:
                maxi = max(maxi, difflis[-1][0]-difflis[1][0])
            elif inx2==n-1:
                maxi = max(maxi, difflis[-2][0]-difflis[0][0])
            else:
                maxi = max(maxi, difflis[-1][0]-difflis[0][0])
            tmini = min(tmini, maxi)
        return tmini
