class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int>ss ;
        multiset<int> diff ;
        for(auto &it:points){
            ss.insert(it[0]+it[1]) ;
            diff.insert(it[0]-it[1]) ;
        }
        int ans = 1e9 ;
        for(int i=0;i<points.size();i++){
            int sum = points[i][0]+points[i][1] ;
            int df = points[i][0]-points[i][1] ;
            ss.erase(ss.find(sum)) ;
            diff.erase(diff.find(df)) ;
          int temp = max(*ss.rbegin()-*ss.begin(),*diff.rbegin()-*diff.begin()) ;
            ans = min(ans,temp) ;
            ss.insert(sum) ;
            diff.insert(df) ;
        }
        return ans ;
    }
};
