class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        map<int, int> x1, x2, x3, x4;
        for (auto &p : points) {
            int x = p[0], y = p[1];
            x1[x + y]++;
            x2[x - y]++;
            x3[-x + y]++;
            x4[-x - y]++;
        }
        int res = INT_MAX;
        for (auto &p : points) {
            int x = p[0], y = p[1];
            x1[x + y]--;
            if (!x1[x + y]) x1.erase(x + y);
            x2[x - y]--;
            if (!x2[x - y]) x2.erase(x - y);
            x3[-x + y]--;
            if (!x3[-x + y]) x3.erase(-x + y);
            x4[-x - y]--;
            if (!x4[-x - y]) x4.erase(-x - y);
            int mx = prev(x1.end())->first - x1.begin()->first;
            mx = max(mx, prev(x2.end())->first - x2.begin()->first);
            mx = max(mx, prev(x3.end())->first - x3.begin()->first);
            mx = max(mx, prev(x4.end())->first - x4.begin()->first);
            res = min(res, mx);
            x1[x + y]++;
            x2[x - y]++;
            x3[-x + y]++;
            x4[-x - y]++;
        }
        return res;
    }
};
