class Solution {
public:
    int minimumDistance(vector<vector<int>>& a) {


        auto solve=[&](int idx){
            int mx1=-1e9,mn1=1e9;
            int mx2=-1e9,mn2=1e9;
            for(int i=0;i<a.size();i++)if(i!=idx){
                mx1=max(mx1, a[i][0]+a[i][1]);
                mn1=min(mn1, a[i][0]+a[i][1]);
                mx2=max(mx2, a[i][0]-a[i][1]);
                mn2=min(mn2, a[i][0]-a[i][1]);
            }
            return max(mx1-mn1, mx2-mn2);
        };
        int ans=1e9;   
        {
        map<int, vector<int>> m;
        vector<int> v;
        for(int i=0;i<a.size();i++){
            m[a[i][0]+a[i][1]].push_back(i);
            v.push_back(a[i][0]+a[i][1]);
        }
        sort(v.begin(),v.end());
        ans=min(ans, solve(m[v[0]][0]));
        ans=min(ans, solve(m[v[v.size()-1]][0]));
        }
        {
            map<int, vector<int>> m;
            vector<int> v;
            for(int i=0;i<a.size();i++){
                m[a[i][0]-a[i][1]].push_back(i);
                v.push_back(a[i][0]-a[i][1]);
            }
            sort(v.begin(),v.end());
            ans=min(ans, solve(m[v[0]][0]));
            ans=min(ans, solve(m[v[v.size()-1]][0]));
        }
        return ans;

    }
};
