class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int ans=INT_MAX;
        map<int,int>m,p;
        for(auto x:points)
        {
            m[x[0]+x[1]]++;
            p[x[0]-x[1]]++;
        }
        for(auto x:points)
        {
            int xx=x[0]+x[1];
            int yy=x[0]-x[1];
            m[xx]--;
            p[yy]--;
            if(m[xx]==0)
            {
                m.erase(xx);
            }
            if(p[yy]==0)
            {
                p.erase(yy);
            }
            auto a=m.rbegin();
            int mx1=a->first;
            auto b=m.begin();
            int mn1=b->first;
            auto c=p.rbegin();
            int mx2=c->first;
            auto d=p.begin();
            int mn2=d->first;
            int result=max(mx1-mn1,mx2-mn2);
            ans=min(result,ans);
            m[xx]++;
            p[yy]++;
        }
        return ans;
    }
};
