class Solution {
public:
    typedef long long ll;
    int minimumDistance(vector<vector<int>>& points) {
        ll n=points.size();
        sort(points.begin(),points.end());
        ll mn1[n],mn2[n],mx1[n],mx2[n];
        ll a=LLONG_MAX,b=LLONG_MAX,f=LLONG_MIN,g=LLONG_MIN;
        for(ll i=0;i<n;i++){
            ll w=points[i][0]+points[i][1];
            ll w2=points[i][0]-points[i][1];
            a=min(a,w);
            f=max(f,w);
            b=min(b,w2);
            g=max(g,w2);
            mn1[i]=a;
            mn2[i]=b;
            mx1[i]=f;
            mx2[i]=g;            

        }
         ll mnx1[n],mnx2[n],mxx1[n],mxx2[n];
         a=LLONG_MAX,b=LLONG_MAX,f=LLONG_MIN,g=LLONG_MIN;
        for(ll i=n-1;i>=0;i--){
            ll w=points[i][0]+points[i][1];
            ll w2=points[i][0]-points[i][1];
            a=min(a,w);
            f=max(f,w);
            b=min(b,w2);
            g=max(g,w2);
            mnx1[i]=a;
            mnx2[i]=b;
            mxx1[i]=f;
            mxx2[i]=g;            

        }
        ll dp[n];
        ll ans=LLONG_MAX;
        for(ll i=0;i<n;i++){
            ll op=LLONG_MIN;
                 if(i!=n-1){
                   ll x=mxx1[i+1]-mnx1[i+1];
                     op=max(op,x);
                     x=mxx2[i+1]-mnx2[i+1];
                     op=max(op,x);
                 }
            if(i!=0){
                  ll x=mx1[i-1]-mn1[i-1];
                     op=max(op,x);
                     x=mx2[i-1]-mn2[i-1];
                     op=max(op,x);
            }
            if(i!=0&&i!=n-1){
                  ll x=max(mxx1[i+1],mx1[i-1]);
                  ll y=min(mnx1[i+1],mn1[i-1]);
                op=max(op,x-y);
                  x=max(mxx2[i+1],mx2[i-1]);
                  y=min(mnx2[i+1],mn2[i-1]);
                op=max(op,x-y);

            }
            ans=min(ans,op);

        }
        return ans;





    }
};
