class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int ans = 1e9;
        multiset <int> sum, diff;
        for (auto point: points) {
            sum.insert(point[0] + point[1]);
            diff.insert(point[0] - point[1]);
        }
        for (auto point: points) {
            sum.erase(sum.find(point[0] + point[1]));
            diff.erase(diff.find(point[0] - point[1]));
            ans = min(ans, max(*sum.rbegin() - *sum.begin(), *diff.rbegin() - *diff.begin()));
            sum.insert(point[0] + point[1]);
            diff.insert(point[0] - point[1]);
        }
        return ans;
    }
};
