class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int maxsum = points[0][0] + points[0][1];
        int argmaxsum = 0;
        int minsum = maxsum;
        int argminsum = 0;
        int maxdiff = points[0][0] - points[0][1];
        int argmaxdiff = 0;
        int mindiff = maxdiff;
        int argmindiff = 0;

        int i = 0;
        for (vector<int>& point : points) {
            int x = point[0];
            int y = point[1];

            int sum = x + y;
            int diff = x - y;

            if (sum > maxsum) {
                maxsum = sum;
                argmaxsum = i;
            }
            if (sum < minsum) {
                minsum = sum;
                argminsum = i;
            }
            if (diff > maxdiff) {
                maxdiff = diff;
                argmaxdiff = i;
            }
            if (diff < mindiff) {
                mindiff = diff;
                argmindiff = i;
            }



            i++;
        }

        return min(min(maxDistance(points, argmaxsum), maxDistance(points, argminsum)), min(maxDistance(points, argmaxdiff), maxDistance(points, argmindiff)));
    }

    int maxDistance(vector<vector<int>>& points, int ignore) {
        int maxsum;
        int minsum;
        int maxdiff;
        int mindiff;

        if (ignore == 0) {
            maxsum = points[1][0] + points[1][1];
            minsum = maxsum;
            maxdiff = points[1][0] - points[1][1];
            mindiff = maxdiff;
        } else {
            maxsum = points[0][0] + points[0][1];
            minsum = maxsum;
            maxdiff = points[0][0] - points[0][1];
            mindiff = maxdiff;
        }

        for (int i = 0; i < points.size(); i++) {
            if (i == ignore) {
                continue;
            }

            int sum = points[i][0] + points[i][1];
            int diff = points[i][0] - points[i][1];

            maxsum = max(maxsum, sum);
            minsum = min(minsum, sum);
            maxdiff = max(maxdiff, diff);
            mindiff = min(mindiff, diff);
        }

        return max(maxsum - minsum, maxdiff - mindiff);
    }
};
