class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int N = A.size();
        int minsum, maxsum, mindiff, maxdiff;
        int minsumIdx = 0, maxsumIdx = 0, mindiffIdx = 0, maxdiffIdx = 0;

        minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];

        for (int i = 1; i < N; i++) {
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum) {
                minsum = sum;
                minsumIdx = i;
            } else if (sum > maxsum) {
                maxsum = sum;
                maxsumIdx = i;
            }
            if (diff < mindiff) {
                mindiff = diff;
                mindiffIdx = i;
            } else if (diff > maxdiff) {
                maxdiff = diff;
                maxdiffIdx = i;
            }
        }

        int target1 = -1, target2 = -1;
        if (maxsum - minsum > maxdiff - mindiff) {
            target1 = maxsumIdx;
            target2 = minsumIdx;
        } else {
            target1 = maxdiffIdx;
            target2 = mindiffIdx;
        }

        int a = helper(A, target1);
        int b = helper(A, target2);

        return min(a, b);
    }

private:
    int helper(vector<vector<int>>& A, int x) {
        int N = A.size();
        int minsum, maxsum, mindiff, maxdiff;
        int minsumIdx = 0, maxsumIdx = 0, mindiffIdx = 0, maxdiffIdx = 0;

        if (x != 0) {
            minsum = maxsum = A[0][0] + A[0][1];
            mindiff = maxdiff = A[0][0] - A[0][1];
        } else {
            minsum = maxsum = A[1][0] + A[1][1];
            mindiff = maxdiff = A[1][0] - A[1][1];
        }

        for (int i = 0; i < N; i++) {
            if (i == x)
                continue;
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum) {
                minsum = sum;
                minsumIdx = i;
            } else if (sum > maxsum) {
                maxsum = sum;
                maxsumIdx = i;
            }
            if (diff < mindiff) {
                mindiff = diff;
                mindiffIdx = i;
            } else if (diff > maxdiff) {
                maxdiff = diff;
                maxdiffIdx = i;
            }
        }

        return max(maxsum - minsum, maxdiff - mindiff);
    }
};
