class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<long long> dist;
        multiset<long long> first;
        multiset<long long> second;
        for (auto i : points) {
            first.insert(i[0] + i[1]);
            second.insert(i[0] - i[1]);
        }
        for (auto i : points) {
            first.erase(first.find(i[0] + i[1]));
            second.erase(second.find(i[0] - i[1]));
            dist.push_back(max(*--first.end() - *first.begin(), *--second.end() - *second.begin()));
            first.insert({i[0] + i[1]});
            second.insert({i[0] - i[1]});
        }
        sort(dist.begin(), dist.end());
        return dist[0];
    }
};
