class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        multiset<int> xs, ys;
        for (auto& v : points) {
            xs.insert(v[0] + v[1]);
            ys.insert(v[0] - v[1]);
        }

        int ans = 0x3F3F3F3F;
        for (auto& v : points) {
            xs.erase(xs.find(v[0] + v[1]));
            ys.erase(ys.find(v[0] - v[1]));

            ans = min(ans, max(*xs.rbegin() - *xs.begin(), *ys.rbegin() - *ys.begin()));

            xs.insert(v[0] + v[1]);
            ys.insert(v[0] - v[1]);
        }
        return ans;
    }
};
