class Solution {
public:

    int cal(int t1,int t2,vector<vector<int>>& points){
        int n = points.size();
        int ans = INT_MAX;
        int min1 = INT_MAX,max1 = INT_MIN;
        int min2 = INT_MAX,max2 = INT_MIN;
        for(int i=0;i<n;i++){
            if(i == t1) continue;
            int x = points[i][0], y = points[i][1];
            if(x + y > max1){
                max1 = x + y;
            }
            if(x + y < min1){
                min1 = x + y;
            }
        }

        for(int i=0;i<n;i++){
            if(i == t1) continue;
            int x = points[i][0], y = points[i][1];
            if(y - x > max2){
                max2 = y - x;
            }
            if(y - x < min2){
                min2 = y - x;
            }
        }
        cout << min1 << " " << max1 << " " << min2 << " " << max2 << endl;
        ans = min(ans,max(max2 - min2,max1 - min1));

        min1 = INT_MAX;
        max1 = INT_MIN;
        min2 = INT_MAX;
        max2 = INT_MIN;
        for(int i=0;i<n;i++){
            if(i == t2) continue;
            int x = points[i][0], y = points[i][1];
            if(y - x > max2){
                max2 = y - x;
            }
            if(y - x < min2){
                min2 = y - x;
            }
        }

        for(int i=0;i<n;i++){
            if(i == t2) continue;
            int x = points[i][0], y = points[i][1];
            if(x + y > max1){
                max1 = x + y;
            }
            if(x + y < min1){
                min1 = x + y;
            }
        }
        cout << min1 << " " << max1 << " " << min2 << " " << max2 << endl;
        ans = min(ans,max(max2 - min2,max1 - min1));
        return ans;
    }

    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();

        int min1 = INT_MAX,max1 = INT_MIN;
        int min1_idx,max1_idx;

        for(int i=0;i<n;i++){
            int x = points[i][0], y = points[i][1];
            if(x + y > max1){
                max1_idx = i;
                max1 = x + y;
            }
            if(x + y < min1){
                min1_idx = i;
                min1 = x + y;
            }
        }

        int min2 = INT_MAX,max2 = INT_MIN;
        int min2_idx,max2_idx;

        for(int i=0;i<n;i++){
            int x = points[i][0], y = points[i][1];
            if(y - x > max2){
                max2_idx = i;
                max2 = y - x;
            }
            if(y - x < min2){
                min2_idx = i;
                min2 = y - x;
            }
        }

        int f1,f2;
        if(max1 - min1 >= max2 - min2){
            f1 = min1_idx;
            f2 = max1_idx;
        }
        else{
            f1 = min2_idx;
            f2 = max2_idx;
        }
        cout << f1 << " " << f2 << endl;
        return cal(f1,f2,points);
    }
};
