class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        a = [(x + y, i ) for i, (x, y) in enumerate(points)]
        a.sort()
        b = [(x - y, i ) for i, (x, y) in enumerate(points)]
        b.sort()
        ans = inf
        n = len(points)
        for i in range(n):
            a1 = None
            if i == a[0][1]:
                a1 = a[-1][0] - a[1][0]
            elif i == a[-1][1]:
                a1 = a[-2][0] - a[0][0]
            else:
                a1 = a[-1][0] - a[0][0]
            b1 = None
            if i == b[0][1]:
                b1 = b[-1][0] - b[1][0]
            elif i == b[-1][1]:
                b1 = b[-2][0] - b[0][0]
            else:
                b1 = b[-1][0] - b[0][0]
            ans = min(ans, max(a1, b1))
        return ans
