class Solution {
public:
    int minimumDistance(vector<vector<int>>& arr, multiset<int> s1 = {}, multiset<int> s2 = {},int res = INT_MAX) {
        for(int i = 0;i<arr.size();i++)s1.insert(arr[i][0] +arr[i][1]), s2.insert(arr[i][0] - arr[i][1]);
        for(int i =0; i<arr.size();i++){
            s1.erase(s1.find(arr[i][0] + arr[i][1])),  s2.erase(s2.find(arr[i][0] - arr[i][1]));
            res = min(res, max( (*s1.rbegin()) - (*s1.begin()), (*s2.rbegin()) - (*s2.begin()) ));
            s1.insert(arr[i][0] + arr[i][1]), s2.insert(arr[i][0] - arr[i][1]);
        }
        return res;
    }
};
