class Solution {
public:
    int f(vector<vector<int>> &x,int idx){
        int l,r;
        int n=x.size();
        if(x[0][1]==idx){
            l=x[1][0];
        }
        else{
            l=x[0][0];
        }
        if(x[n-1][1]==idx){
            r=x[n-2][0];
        }
        else{
            r=x[n-1][0];
        }
        return r-l;
    }
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector<vector<int>> x(n,vector<int> (2));
        vector<vector<int>> y(n,vector<int> (2));
        for(int i=0;i<n;++i){
            x[i]={points[i][0]+points[i][1],i};
            y[i]={points[i][0]-points[i][1],i};
        }
        sort(x.begin(),x.end());
        sort(y.begin(),y.end());
        int mina=1e9;
        for(int i=0;i<n;++i){
            int ans1,ans2;
            ans1=f(x,i);
            ans2=f(y,i);
            int maxa=max(ans1,ans2);
            mina=min(maxa,mina);
        }
        return mina;
    }
};
