class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int,int>> v;
        for(auto &u: points){
            v.emplace_back(u[0], u[1]);
        }
        auto f = [&](vector<pair<int,int>>& v){
            int mx = 0, n = v.size(), x = 0, y = 0;
            auto dis = [&](int x, int y){
                return abs(v[x].first - v[y].first) + abs(v[x].second - v[y].second);
            };
            vector<pair<int,int>> vv;
            for(int i = 0; i < n; i++){
                vv.emplace_back(v[i].first, i);
            }
            sort(vv.begin(), vv.end());
            if(mx < dis(vv[0].second, vv[n-1].second)){
                mx = dis(vv[0].second, vv[n-1].second);
                x = vv[0].second, y = vv[n-1].second;
            }
            vv.clear();
            for(int i = 0; i < n; i++){
                vv.emplace_back(v[i].second, i);
            }
            sort(vv.begin(), vv.end());
            if(mx < dis(vv[0].second, vv[n-1].second)){
                mx = dis(vv[0].second, vv[n-1].second);
                x = vv[0].second, y = vv[n-1].second;
            }
            vv.clear();
            for(int i = 0; i < n; i++){
                vv.emplace_back(v[i].first + v[i].second, i);
            }
            sort(vv.begin(), vv.end());
            if(mx < dis(vv[0].second, vv[n-1].second)){
                mx = dis(vv[0].second, vv[n-1].second);
                x = vv[0].second, y = vv[n-1].second;
            }
            vv.clear();
            for(int i = 0; i < n; i++){
                vv.emplace_back(v[i].first - v[i].second, i);
            }
            sort(vv.begin(), vv.end());
            if(mx < dis(vv[0].second, vv[n-1].second)){
                mx = dis(vv[0].second, vv[n-1].second);
                x = vv[0].second, y = vv[n-1].second;
            }
            return make_pair(x, y);
        };
        auto dis = [&](vector<pair<int,int>>& v, int x, int y){
            return abs(v[x].first - v[y].first) + abs(v[x].second - v[y].second);
        };
        auto [x, y] = f(v);
        auto v1 = v, v2 = v;
        v1.erase(v1.begin() + x);
        v2.erase(v2.begin() + y);
        auto [xx, yy] = f(v1);
        auto [xxx, yyy] = f(v2);
        return min(dis(v1,xx,yy), dis(v2,xxx,yyy));
    }
};
