class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        pair<int, int> AB = getFurthest(points, -1);
        pair<int, int> AB2 = getFurthest(points, AB.first);
        pair<int, int> AB3 = getFurthest(points, AB.second);
        return min(getDist(points, AB2.first, AB2.second), getDist(points, AB3.first, AB3.second));
    }

private:
    pair<int, int> getFurthest(vector<vector<int>>& points, int exclude) {
        int n = points.size();
        int idxa, idxb, idxc, idxd;
        int maxa, maxb, maxc, maxd;
        maxa = maxb = maxc = maxd = INT_MIN;
        for (int i = 0; i < n; ++i) {
            if (exclude == i) {
                continue;
            }
            int x = points[i][0], y = points[i][1];
            if (-x - y > maxa) {
                maxa = -x -y;
                idxa = i;
            }
            if (-x + y > maxb) {
                maxb = -x + y;
                idxb = i;
            }
            if (x - y > maxc) {
                maxc = x - y;
                idxc = i;
            }
            if (x + y > maxd) {
                maxd = x + y;
                idxd = i;
            }
        }
        int maxdist = INT_MIN;
        int idxA, idxB;
        if (getDist(points, idxa, idxb) > maxdist) {
            maxdist = getDist(points, idxa, idxb);
            idxA = idxa;
            idxB = idxb;
        }
        if (getDist(points, idxa, idxc) > maxdist) {
            maxdist = getDist(points, idxa, idxc);
            idxA = idxa;
            idxB = idxc;
        }
        if (getDist(points, idxa, idxd) > maxdist) {
            maxdist = getDist(points, idxa, idxd);
            idxA = idxa;
            idxB = idxd;
        }
        if (getDist(points, idxb, idxc) > maxdist) {
            maxdist = getDist(points, idxb, idxc);
            idxA = idxb;
            idxB = idxc;
        }
        if (getDist(points, idxb, idxd) > maxdist) {
            maxdist = getDist(points, idxb, idxd);
            idxA = idxb;
            idxB = idxd;
        }
        if (getDist(points, idxc, idxd) > maxdist) {
            maxdist = getDist(points, idxc, idxd);
            idxA = idxc;
            idxB = idxd;
        }
        return {idxA, idxB};
    }

    int getDist(vector<vector<int>>& points, int idx1, int idx2) {
        return abs(points[idx1][0] - points[idx2][0]) + abs(points[idx1][1] - points[idx2][1]);
    }
};
