class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        vector <pair <int, int>> sum(n),diff(n);
        for (int i=0;i<n;i++)
        {
            sum[i]={points[i][0]+points[i][1],i};
            diff[i]={points[i][0]-points[i][1],i};
        }
        sort(sum.begin(),sum.end());
        sort(diff.begin(),diff.end());
        int x=max(sum[n-1].first-sum[0].first,diff[n-1].first-diff[0].first);
        if (x==0)
        {
            return 0;
        }
        int ans=1e9;
        if (x==sum[n-1].first-sum[0].first)
        {
            vector <int> sum1,sum2,diff1,diff2;
            for (int i=0;i<n;i++)
            {
                if (i!=sum[0].second)
                {
                    sum1.push_back(points[i][0]+points[i][1]);
                    diff1.push_back(points[i][0]-points[i][1]);
                }
                if (i!=sum[n-1].second)
                {
                    sum2.push_back(points[i][0]+points[i][1]);
                    diff2.push_back(points[i][0]-points[i][1]);
                }
            }
            sort(sum1.begin(),sum1.end());
            sort(diff1.begin(),diff1.end());
            sort(sum2.begin(),sum2.end());
            sort(diff2.begin(),diff2.end());
            ans=min(ans,max(sum1[n-2]-sum1[0],diff1[n-2]-diff1[0]));
            ans=min(ans,max(sum2[n-2]-sum2[0],diff2[n-2]-diff2[0]));
        }
        if (x==diff[n-1].first-diff[0].first)
        {
            vector <int> sum1,sum2,diff1,diff2;
            for (int i=0;i<n;i++)
            {
                if (i!=diff[0].second)
                {
                    sum1.push_back(points[i][0]+points[i][1]);
                    diff1.push_back(points[i][0]-points[i][1]);
                }
                if (i!=diff[n-1].second)
                {
                    sum2.push_back(points[i][0]+points[i][1]);
                    diff2.push_back(points[i][0]-points[i][1]);
                }
            }
            sort(sum1.begin(),sum1.end());
            sort(diff1.begin(),diff1.end());
            sort(sum2.begin(),sum2.end());
            sort(diff2.begin(),diff2.end());
            ans=min(ans,max(sum1[n-2]-sum1[0],diff1[n-2]-diff1[0]));
            ans=min(ans,max(sum2[n-2]-sum2[0],diff2[n-2]-diff2[0]));
        }
        return ans;
    }
};
