class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int N=A.size();
        set<pair<int,int>> st1, st2;

        for (int i = 0; i < N; i++) {
            st1.insert({A[i][0] + A[i][1],i});
            st2.insert({A[i][0] - A[i][1],i});
        }


        int mxi=INT_MAX;
        for(int i=0;i<N;i++){
            st1.erase({A[i][0] + A[i][1],i});
            st2.erase({A[i][0] - A[i][1],i});
            int t=max({prev(st1.end())->first - st1.begin()->first, prev(st2.end())->first - st2.begin()->first});
            mxi=min(mxi,t);
            st1.insert({A[i][0] + A[i][1],i});
            st2.insert({A[i][0] - A[i][1],i});
        }

        return mxi;
    }
};
