class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        int ans = 1e9;

        multiset<int> st1, st2;
        for (auto x : points) st1.insert(x[0] + x[1]);
        for (auto x : points) st2.insert(x[0] - x[1]);

        for (int i = 0; i < n; i++) {
            int x = points[i][0], y = points[i][1];
            int p1 = x + y, p2 = x - y;

            st1.erase(st1.find(p1));
            st2.erase(st2.find(p2));

            int dist = max(*st1.rbegin() - *st1.begin(), *st2.rbegin() - *st2.begin());
            ans = min(ans, dist);

            st1.insert(p1);
            st2.insert(p2);
        }

        return ans;
    }
};
