class Solution {
public:
    int f(vector<vector<int>>& a)
    {
        int n=a.size();
        multiset<int> st1,st2;
        for (int i=0;i<n;i++) {
            st1.insert(a[i][0] + a[i][1]);
            st2.insert(a[i][0] - a[i][1]);
        }
        int mn=1e9;
        for(int i=0;i<n;i++)
        {
            int s=a[i][0]+a[i][1];
            int d=a[i][0]-a[i][1];
            st1.erase(st1.find(s));
            st2.erase(st2.find(d));
            int mx=max(*prev(st1.end())-*st1.begin(),*prev(st2.end())-*st2.begin());
            mn=min(mx,mn);
            st1.insert(s);
            st2.insert(d);
        }
        return mn;
    }
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        int ans=f(points);
        return ans;
    }
};
