#define x first
#define y second

class Solution {
public:

    typedef pair<int, int> PII;
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size();
        vector<vector<int>> w(4);
        vector<vector<PII>> q(4);
        for(int i = 0; i < n; i ++){

            w[0].push_back(p[i][0] + p[i][1]);
            q[0].push_back({p[i][0] + p[i][1], i});
            w[1].push_back(p[i][0] - p[i][1]);
            q[1].push_back({p[i][0] - p[i][1], i});
            w[2].push_back(-p[i][0] + p[i][1]);
            q[2].push_back({-p[i][0] + p[i][1], i});
            w[3].push_back(-p[i][0] - p[i][1]);
            q[3].push_back({-p[i][0] - p[i][1], i});
        }


        for(int i = 0; i < 4; i ++){
            sort(w[i].begin(), w[i].end());
            sort(q[i].begin(), q[i].end());
        }
        int ans = 1e9;

        for(int i = 0; i < n; i ++){
            int maxn = 0;
            for(int j = 0; j < 4; j ++){
                if(q[j][n-1].y == i){
                    maxn = max(maxn, q[j][n-2].x - q[j][0].x);
                }
                else if(q[j][0].y == i){
                    maxn = max(maxn, q[j][n-1].x - q[j][1].x);
                }
                else{
                    maxn = max(maxn, q[j][n-1].x - q[j][0].x);
                }

            }

            ans = min(ans, maxn);
        }

        return ans;
    }
};
