class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n = points.size();
        vector<pair<int, int>> sum(n);
        vector<pair<int, int>> diff(n);

        for(int i=0; i<n; i++){
            sum[i] = {(points[i][0] + points[i][1]), i};
            diff[i] ={(points[i][0] - points[i][1]), i};
        }

        sort(sum.begin(), sum.end());
        sort(diff.begin(), diff.end());

        if(diff[n-1].first - diff[0].first > sum[n-1].first - sum[0].first){
            int rmI = diff[n-1].second;
            int minm = diff[n-2].first - diff[0].first;
            if(sum[0].second == rmI){
                minm = max(minm, sum[n-1].first - sum[1].first);
            }else if(sum[n-1].second == rmI){
                minm = max(minm, sum[n-2].first - sum[0].first);
            }else{
                minm = max(minm, sum[n-1].first - sum[0].first);
            }

            int rmI2 = diff[0].second;
            int minm2 = diff[n-1].first - diff[1].first;

            if(sum[0].second == rmI2){
                minm2 = max(minm2, sum[n-1].first - sum[1].first);
            }else if(sum[n-1].second == rmI2){
                minm2 = max(minm2, sum[n-2].first - sum[0].first);
            }else{
                minm2 = max(minm2, sum[n-1].first - sum[0].first);
            }

            return min(minm, minm2);
        }else{
            int rmI = sum[n-1].second;
            int minm = sum[n-2].first - sum[0].first;
            if(diff[0].second == rmI){
                minm = max(minm, diff[n-1].first - diff[1].first);
            }else if(diff[n-1].second == rmI){
                minm = max(minm, diff[n-2].first - diff[0].first);
            }else{
                minm = max(minm, diff[n-1].first - diff[0].first);
            }

            int rmI2 = sum[0].second;
            int minm2 = sum[n-1].first - sum[1].first;

            if(diff[0].second == rmI2){
                minm2 = max(minm2, diff[n-1].first - diff[1].first);
            }else if(diff[n-1].second == rmI2){
                minm2 = max(minm2, diff[n-2].first - diff[0].first);
            }else{
                minm2 = max(minm2, diff[n-1].first - diff[0].first);
            }

            return min(minm, minm2);
        }
    }
};
