class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        ans=0
        re_i1=0
        re_i2=0
        for k in range(4):
            r=-1e10
            l=1e10;
            i1=0
            i2=0
            for i in range(len(points)):
                ps=0
                if ((k&1)==1):
                    ps+=points[i][0]
                else:
                    ps-=points[i][0]
                if ((k&2)==0):
                    ps+=points[i][1]
                else:
                    ps-=points[i][1]
                if (ps>r):
                    r=ps
                    i1=i
                if (ps<l):
                    l=ps
                    i2=i
                '''
                if (ps>r):
                    l=r
                    i2=i1
