class Solution {
public:

    int minimumDistance(vector<vector<int>>& nums) {
        map<int,int> cnt1,cnt2;
        set<int> mx,mn;
        int n=nums.size();
        for(int i=0;i<n;i++){
            int x=nums[i][0];
            int y=nums[i][1];
            mx.insert(x+y);
            cnt1[x+y]++;
            mn.insert(x-y);
            cnt2[x-y]++;
        }
        int ans=1e9;
        int a=1e9;
        for(int i=0;i<n;i++){
            int x=nums[i][0];
            int y=nums[i][1];
            cnt1[x+y]--;
            if(cnt1[x+y]==0){
                mx.erase(x+y);
            }
            cnt2[x-y]--;
            if(cnt2[x-y]==0){
                mn.erase(x-y);
            }
            auto itr=mx.end();
            itr--;
            int a=*itr;
            int b=*mx.begin();
            itr=mn.end();
            itr--;
            int c=*itr;
            int d=*mn.begin();
            int test=max(a-b,c-d);
            ans=min(ans,test);
            cnt1[x+y]++;
            mx.insert(x+y);
            cnt2[x-y]++;
            mn.insert(x-y);
        }
        return ans;
    }
};
