class Solution {
public:
    int minimumDistance(vector<vector<int>>& points)
    {

        multiset<int>st1,st2; 
        int minn = INT_MAX;
        for(auto it : points)
        {
            st1.insert(it[0]+it[1]);
            st2.insert(it[0]-it[1]);
        }

        for(auto it : points)
        {
            int x = it[0]+it[1];
            int y = it[0]-it[1];
            auto it1 = st1.find(x);
            auto it2 = st2.find(y);
            st1.erase(it1);
            st2.erase(it2);
            int mx= max(*st1.rbegin() - *st1.begin(), *st2.rbegin() - *st2.begin());
            if(minn > mx) minn=mx;
            st1.insert(x);
            st2.insert(y);
        }
        return minn;

    }
};
