typedef pair<int,int> PII;
class Solution {
public:
    vector<int> getMax(vector<vector<int>>& p)
    {
        int n=p.size();
        vector<PII> p1(n),p2(n);
        for(int i=0;i<p.size();i++)
        {
            p1[i]={p[i][0]+p[i][1],i};
            p2[i]={p[i][0]-p[i][1],i};
        }
        sort(p1.begin(),p1.end());
        sort(p2.begin(),p2.end());
        int ans1=p1[n-1].first-p1[0].first;
        int ans2=p2[n-1].first-p2[0].first;
        //cout<<ans1<<" "<<ans2<<endl;
        if(ans1>ans2)
            return {ans1,p1[0].second,p1[n-1].second};
        else return {ans2,p2[0].second,p2[n-1].second};

    }
    int minimumDistance(vector<vector<int>>& points) {
        vector<int> temp=getMax(points);
        //cout<<temp[0]<<endl;
        vector<vector<int>> tmp=points;
        tmp.erase(tmp.begin()+temp[1]);
        int res=1e9;
        vector<int> tp=getMax(tmp);
        res=min(res,tp[0]);
        tmp=points;
        tmp.erase(tmp.begin()+temp[2]);
        tp=getMax(tmp);
        res=min(res,tp[0]);
        return res;

    }
};
