class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        # x0 <= x1, y0 <= y1: (x1 - x0) + (y1 - y0) = (x1 + y1) - (x0 + y0) => min(x0 + y0)
        # x0 <= x1, y0 > y1: (x1 - x0) + (y0 - y1) = (x1 - y1) - (x0 - y0)  => min(x0 - y0)
        # x0 >= x1, y0 <= y1: (x0 - x1) + (y1 - y0) = (y1 - x1) - (y0 - x0) => max(x0 - y0)
        # x0 >= x1, y0 > y1: (x0 - x1) + (y0 - y1) = (x0 + y0) - (y1 + x1)  => max(x0 + y0)

        def solve(ban = -1):
            res, idx1, idx2 = -inf, -1, -1
            mini, min_y, min_idx = inf, inf, -1
            for i, (x, y) in enumerate(points):
                if i == ban:
                    continue
                if min_y <= y:
                    val = x + y - mini
                    if val > res:
                        res, idx1, idx2 = val, i, min_idx
                if x + y < mini:
                    mini, min_y, min_idx = x + y, y, i
            mini, min_y, min_idx = inf, inf, -1
            for i, (x, y) in enumerate(points):
                if i == ban:
                    continue
                if min_y >= y:
                    val = (x - y) - mini
                    if val > res:
                        res, idx1, idx2 = val, i, min_idx
                if x - y < mini:
                    mini, min_y, min_idx = x - y, y, i
            maxi, max_y, max_idx = -inf, -inf, -1
            for i in range(n-1, -1, -1):
                if i == ban:
                    continue
                x, y = points[i]
                if max_y <= y:
                    val = maxi - (x - y)
                    if val > res:
                        res, idx1, idx2 = val, i, max_idx
                if x - y > maxi:
                    maxi, max_y, max_idx = x - y, y, i
            maxi, max_y, max_idx = -inf, -inf, -1
            for i in range(n-1, -1, -1):
                if i == ban:
                    continue
                x, y = points[i]
                if max_y >= y:
                    val = maxi - (x + y)
                    if val > res:
                        res, idx1, idx2 = val, i, max_idx
                if x + y > maxi:
                    maxi, max_y, max_idx = x + y, y, i
            return res, idx1, idx2

        points.sort()
        n = len(points)
        vis = [True] * n
        best, idx1, idx2 = solve()
        res = min(solve(ban = idx1)[0], solve(ban = idx2)[0])
        return res
