class Solution:
    def minimumDistance(self, points: List[List[int]]) -> int:
        res = []

        t1,t2 = defaultdict(list),defaultdict(list)
        l = len(points)
        for i in range(l):
            t1[points[i][0]-points[i][1]].append(i)
            t2[points[i][0]+points[i][1]].append(i)
        s1 = max(t1.keys()) - min(t1.keys()) 
        s2 = max(t2.keys()) - min(t2.keys())
        if s1 > s2:
            r1,r2 = t1[max(t1.keys())],t1[min(t1.keys())] 
        elif s1 < s2:
            r1,r2 = t2[max(t2.keys())],t2[min(t2.keys())] 
        if s1 == s2:
            r1,r2 = t1[max(t1.keys())],t1[min(t1.keys())]
            r3,r4 = t2[max(t2.keys())],t2[min(t2.keys())]
            if len(r1) > 1 and len(r2) > 1:return max(s1,s2)
            if len(r1) > len(r2):r1,r2 = r2,r1



            t1,t2 = [],[]
            for i in range(l):
                if i != r1[0]:
                    t1.append(points[i][0]-points[i][1])
                    t2.append(points[i][0]+points[i][1])
            res.append(max(max(t1) - min(t1), max(t2) - min(t2)))

            if len(r2) == 1:
                t1,t2 = [],[]
                for i in range(l):
                    if i != r2[0]:
                        t1.append(points[i][0]-points[i][1])
                        t2.append(points[i][0]+points[i][1])
                res.append(max(max(t1) - min(t1), max(t2) - min(t2)))



            r1,r2 = r3,r4





        if len(r1) > 1 and len(r2) > 1:return max(s1,s2)
        if len(r1) > len(r2):r1,r2 = r2,r1



        t1,t2 = [],[]
        for i in range(l):
            if i != r1[0]:
                t1.append(points[i][0]-points[i][1])
                t2.append(points[i][0]+points[i][1])
        res.append(max(max(t1) - min(t1), max(t2) - min(t2)))

        if len(r2) == 1:
            t1,t2 = [],[]
            for i in range(l):
                if i != r2[0]:
                    t1.append(points[i][0]-points[i][1])
                    t2.append(points[i][0]+points[i][1])
            res.append(max(max(t1) - min(t1), max(t2) - min(t2)))

        return min(res)
