class Solution {
public:
    int findans(vector<vector<int>>&points,int ind){
        vector<int> v,v1;

        for (int i = 0; i < points.size(); i++) {
            if(ind!=i){
                v.push_back(points[i][0] + points[i][1]);
                v1.push_back(points[i][0] - points[i][1]);
            }
        }
        sort(v.begin(), v.end());
        sort(v1.begin(), v1.end());

        int p = v[v.size()-1] - v.front();
        int q = v1[v.size()-1] - v1.front();
        return max(p,q);
    }

    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int,int>> v,v1;

        for (int i = 0; i < points.size(); i++) {
            v.push_back({points[i][0] + points[i][1],i});
            v1.push_back({points[i][0] - points[i][1],i});
        }

        sort(v.begin(), v.end());
        sort(v1.begin(), v1.end());

        int p = v[v.size()-1].first - v.front().first;
        int q = v1[v.size()-1].first - v1.front().first;
        if(p>q) {
            int ind1=v.front().second;
            int ind2=v[v.size()-1].second;
            return min(findans(points,ind1), findans(points,ind2));
        } else {
            int ind1=v1.front().second;
            int ind2=v1[v.size()-1].second;
            return min(findans(points,ind1), findans(points,ind2));
        }
    }
};
