class Solution {
public:
    int minimumDistance(vector<vector<int>>& A) {
        int minsum, maxsum, mindiff, maxdiff;
        int N=A.size();
        minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];
        int a=0,b=0,c=0,d=0;
        for (int i = 1; i < N; i++) {
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum){
                minsum = sum;
                a=i;
            }
            else if (sum > maxsum){
                maxsum = sum;
                b=i;
            }
            if (diff < mindiff){
                mindiff = diff;
                c=i;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
                d=i;
            }
        }
        // cout<<maxsum<<" "<<minsum<<" "<<maxdiff<<" "<<mindiff<<endl;
        int ans=maxsum - minsum;
        int res=maxdiff - mindiff;
        int first,second;
        if(ans>=res){
            first=a;
            second=b;
        }
        else{
            first=c;
            second=d;
        }
        if(first!=0){minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];}
        else{
            minsum = maxsum = A[1][0] + A[1][1];
            mindiff = maxdiff = A[1][0] - A[1][1];
        }
        for (int i = 0; i < N; i++) {
            if(i==first) continue;
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum){
                minsum = sum;
            }
            else if (sum > maxsum){
                maxsum = sum;
            }
            if (diff < mindiff){
                mindiff = diff;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
            }
        }
        int maximum = max(maxsum - minsum, maxdiff - mindiff);
        if(second!=0){minsum = maxsum = A[0][0] + A[0][1];
        mindiff = maxdiff = A[0][0] - A[0][1];}
        else{
            minsum = maxsum = A[1][0] + A[1][1];
            mindiff = maxdiff = A[1][0] - A[1][1];
        }
        for (int i = 0; i < N; i++) {
            if(i==second) continue;
            int sum = A[i][0] + A[i][1];
            int diff = A[i][0] - A[i][1];
            if (sum < minsum){
                minsum = sum;
            }
            else if (sum > maxsum){
                maxsum = sum;
            }
            if (diff < mindiff){
                mindiff = diff;
            }
            else if (diff > maxdiff){
                maxdiff = diff;
            }
        }
        int maximum1 = max(maxsum - minsum, maxdiff - mindiff);
        return min(maximum,maximum1);
    }
};
