class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) 
    {
        int n = points.size();
        long long mx_s = -1e10,mn_s =  1e10,mn_d = 1e10,mx_d = -1e10;
        vector<long long> sum,diff;
        map<long long,long long> mp_s,mp_d;
        for(auto it:points)
        {
            long long s = (it[0] + it[1]);
            long long d = (it[0] - it[1]);
            mp_s[s]++;
            mp_d[d]++;
            sum.push_back(s);
            diff.push_back(d);
            mx_s = max(mx_s,s);
            mn_s = min(mn_s,s);
            mn_d = min(mn_d,d);
            mx_d = max(mx_d,d);
        }
        sort(sum.begin(),sum.end());
        sort(diff.begin(),diff.end());
        long long ans = max(mx_s - mn_s,mx_d - mn_d);
        for(auto it:points)
        {
            long long s = (it[0] + it[1]);
            long long d = (it[0] - it[1]);

            long long mx_s1 = -1e10,mn_s1 =  1e10,mn_d1 = 1e10,mx_d1 = -1e10;

            if(s==sum[0])
            {
                if(mp_s[s]>1)
                mn_s1 = s;
                else
                mn_s1 = sum[1];
            }
            else
            mn_s1 = sum[0];
            if(s==sum[n-1])
            {
                if(mp_s[s]>1)
                mx_s1 = s;
                else
                mx_s1 = sum[n-2];
            }
            else
            mx_s1 = sum[n-1];


            if(d==diff[0])
            {
                if(mp_d[d]>1)
                mn_d1 = d;
                else
                mn_d1 = diff[1];
            }
            else
            mn_d1 = diff[0];
            if(d==diff[n-1])
            {
                if(mp_d[d]>1)
                mx_d1 = d;
                else
                mx_d1 = diff[n-2];
            }
            else
            mx_d1 = diff[n-1];

            long long temp = max(mx_s1 - mn_s1,mx_d1 - mn_d1);
            ans = min(ans,temp);
        }

        return ans;

    }
};
