class Solution {
public:
    int ans=1e9;
    int a[100005],b[100005],minax,minbx,maxax,maxbx;
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        int i=0;
        for(auto x:points){
            ++i;
            a[i]=x[0]+x[1];
            b[i]=x[0]-x[1];
        }
        sort(a+1,a+n+1);
        sort(b+1,b+n+1);
        for(auto x:points){
            int z=x[0]+x[1];
            if(a[1]==z)minax=a[2];
            else minax=a[1];
            if(a[n]==z)maxax=a[n-1];
            else maxax=a[n];
            z=x[0]-x[1];
            if(b[1]==z)minbx=b[2];
            else minbx=b[1];
            if(b[n]==z)maxbx=b[n-1];
            else maxbx=b[n];
ans=min(ans,max(abs(maxax-minax),abs(minbx-maxbx)));}
        return ans;
    }
};
