class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n=points.size();
        int i;
        int ans=INT_MAX;
        vector<pair<int,int>>x1;
        vector<pair<int,int>>y1;
        for(i=0;i<n;i++)
        {
            x1.push_back({points[i][0]-points[i][1],i});
            y1.push_back({points[i][0]+points[i][1],i});
        }
        sort(x1.begin(),x1.end());
        sort(y1.begin(),y1.end());
        int idx1=x1[n-1].second;
        int idx2=x1[0].second;
        int idx3=y1[n-1].second;
        int idx4=y1[0].second;
        ans=min(max(x1[n-1].first-x1[0].first,y1[n-1].first-y1[0].first),ans);
        map<int,int>hash;
        hash[idx1]++;
        hash[idx2]++;
        hash[idx3]++;
        hash[idx4]++;
        int cnt=0;
        for(auto j=hash.begin();j!=hash.end();j++)
        {
            int idx=j->first;
            vector<int>point(2);
            point[0]=points[idx-cnt][0];
            point[1]=points[idx-cnt][1];
            points.erase(points.begin()+idx-cnt);
            vector<int>x;
            vector<int>y;
            for(i=0;i<n-1;i++)
            {
               x.push_back(points[i][0]-points[i][1]);
               y.push_back(points[i][0]+points[i][1]);
            }
             sort(x.begin(),x.end());
             sort(y.begin(),y.end());
            ans=min(ans,max(x[n-2]-x[0],y[n-2]-y[0]));
            points.push_back(point);
            cnt++;
        }
        return ans;
    }
};
