class Solution {
public:

    int minimumDistance(vector<vector<int>>& points) {

        int minS,maxS,minD,maxD,sum,diff;
        int i1=0,i2=0,i3=0,i4=0;
        int idx1,idx2;

        minS=INT_MAX,minD=INT_MAX;
        maxS=INT_MIN,maxD=INT_MIN;

        for(int i=0;i<points.size();i++){
            sum=points[i][0]+points[i][1];
            diff=points[i][0]-points[i][1];

            if(sum<minS){
                minS=sum;
                i1=i;
            }

            if(sum>maxS){
                maxS=sum;
                i2=i;
            }

            if(diff<minD){
                minD=diff;
                i3=i;
            }
            if(diff>maxD){
                maxD=diff;
                i4=i;
            }
        }

        if(maxS-minS >= maxD-minD){
            idx1=i1;
            idx2=i2;
        }
        else{
            idx1=i3;
            idx2=i4;
        }


        minS=INT_MAX,minD=INT_MAX;
        maxS=INT_MIN,maxD=INT_MIN;

        for(int i=0;i<points.size();i++){

            if(i==idx1){
                continue;
            }

            sum=points[i][0]+points[i][1];
            diff=points[i][0]-points[i][1];

            if(sum<minS){
                minS=sum;
                i1=i;
            }

             if(sum>maxS){
                maxS=sum;
                i2=i;
            }

            if(diff<minD){
                minD=diff;
                i3=i;
            }

            if(diff>maxD){
                maxD=diff;
                i4=i;
            }
        }

        int max1=max(maxS-minS,maxD-minD);

        minS=INT_MAX,minD=INT_MAX;
        maxS=INT_MIN,maxD=INT_MIN;

        for(int i=0;i<points.size();i++){

            if(i==idx2){
                continue;
            }

            sum=points[i][0]+points[i][1];
            diff=points[i][0]-points[i][1];

            if(sum<minS){
                minS=sum;
                i1=i;
            }

            if(sum>maxS){
                maxS=sum;
                i2=i;
            }

            if(diff<minD){
                minD=diff;
                i3=i;
            }

            if(diff>maxD){
                maxD=diff;
                i4=i;
            }
        }

        int max2=max(maxS-minS,maxD-minD);

        return min(max1,max2);

    }
};
