class Solution {
public:
    #define pii pair<int,int>
    #define fs first
    #define sc second
    int minimumDistance(vector<vector<int>>& points) {
        vector<pii> v;
        for(auto &i:points)v.push_back(pii(i[0]+i[1],i[0]-i[1]));
        sort(v.begin(),v.end());
        multiset<int> sx,sy;
        for(auto &i:v){
            sx.insert(i.fs);
            sy.insert(i.sc);
        }
        int ans = 2e9;
        for(auto &i:v){
            sx.erase(sx.find(i.fs));
            sy.erase(sy.find(i.sc));
            ans = min(ans,max(*sy.rbegin()-*sy.begin(),*sx.rbegin()-*sx.begin()));
            sx.insert(i.fs);
            sy.insert(i.sc);
        }
        return ans;
    }
};
