class Solution {
public:
    int minimumDistance(vector<vector<int>>& p) {
        int n = p.size();
        multiset<int> mst1, mst2;
        for(auto& e: p){mst1.insert(e[0]+e[1]);mst2.insert(e[0]-e[1]);}
        int mini = 1e9;
        for(auto& e: p){int s = e[0]+e[1], d = e[0]-e[1];mst1.erase(mst1.find(s)); mst2.erase(mst2.find(d));int temp = max(*mst1.rbegin()-*mst1.begin(), *mst2.rbegin()-*mst2.begin());mini = min(mini, temp);mst1.insert(s); mst2.insert(d);}
        return mini;
    }
};
