class Solution {
int maxdist(vector<vector<int>>& v, int exclude)
{
       int minsum, maxsum, mindiff, maxdiff;
        bool init = false;
        for (int i = 0; i < v.size(); i++) {
            if (exclude >= 0 && exclude == i)
                continue;
            if (!init)
            {
                minsum = maxsum = v[i][0] + v[i][1];
                mindiff = maxdiff = v[i][0] - v[i][1];
                init = true;
                continue;
            }
            int sum = v[i][0] + v[i][1];
            int diff = v[i][0] - v[i][1];
            if (sum < minsum)
                minsum = sum;
            else if (sum > maxsum)
                maxsum = sum;
            if (diff < mindiff)
                mindiff = diff;
            else if (diff > maxdiff)
                maxdiff = diff;
        }

        return max(maxsum - minsum, maxdiff - mindiff);
}
vector<int> maxpoints(vector<vector<int>>& v)
{
    vector<int> res{0};
    int minsum, maxsum, mindiff, maxdiff;
    minsum = maxsum = v[0][0] + v[0][1];
    mindiff = maxdiff = v[0][0] - v[0][1];

    for (int i = 1; i < v.size(); i++) {
        int sum = v[i][0] + v[i][1];
        int diff = v[i][0] - v[i][1];
        if (sum < minsum)
        {
            minsum = sum;
            res.push_back(i);
        }
        else if (sum > maxsum)
        {
            maxsum = sum;
            res.push_back(i);
        }
        if (diff < mindiff)
        {
            mindiff = diff;
                        res.push_back(i);
        }
        else if (diff > maxdiff)
        {
            maxdiff = diff;
                        res.push_back(i);
        }
    }
    return res; 
}

public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<int> pt = maxpoints(points);
//        for( int i = 0; i < pt.size(); i++)
 //           cout<< pt[i] << ", ";
  //      cout<< endl;
        int ans = maxdist(points,-1);
        for( int i = 0; i < pt.size(); i++)
        {
            ans = min( ans, maxdist(points, pt[i]) );
        }

        return ans;

    }
};
