class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        vector<pair<int,int>> v;
        multiset<int> sum;
        multiset<int> diff;
        for(auto it:points){
            sum.insert(it[0]+it[1]);
            diff.insert(it[0]-it[1]);
        }
        int ans=INT_MAX;
        int n=points.size();
        for(int i=0;i<n;i++){
            int cursum=points[i][0]+points[i][1];
            int curdiff=points[i][0]-points[i][1];
            sum.erase(sum.find(cursum));
            diff.erase(diff.find(curdiff));
            int curans=INT_MAX;
            int val1=*(sum.rbegin())- *(sum.begin());
            int val2=*(diff.rbegin())- *(diff.begin());
            curans=max(val1,val2);
            ans=min(ans,curans);
            sum.insert(cursum);
            diff.insert(curdiff);
        }

        return ans;
    }
};
