class Solution {
public:
    int minimumDistance(vector<vector<int>>& points) {
        int n= points.size();
        set<pair<int,int>> xaxis, yaxis;

        vector<pair<int,int>> arr;
        for(int i=0; i<n; i++){
            int x= points[i][0], y = points[i][1];

            xaxis.insert({x+y, i});
            yaxis.insert({x-y, i});
            arr.emplace_back(x+y, x-y);
        }

        int ans=INT_MAX;
        for(int i=0; i<n; i++){
            xaxis.erase({arr[i].first, i});
            yaxis.erase({arr[i].second, i});

            int d1 = ((--xaxis.end())->first) - ((xaxis.begin())->first);
            int d2 = ((--yaxis.end())->first) - ((yaxis.begin())->first);

            ans= min(ans, max(d1, d2));
            xaxis.insert({arr[i].first, i});
            yaxis.insert({arr[i].second, i});
        }

        return ans;
    }
};
